mapper.select:
select count(USER_ID)
from ARTICLE
where USER_ID like #{uid}

select ARTSEQ, TITLE, CONTENT, USER_ID as uid
from ARTICLE
where USER_ID like #{uid}

mapper.update:
UPDATE ARTICLE
SET TITLE = #{title}, CONTENT = #{content} 
WHERE ARTSEQ = #{artseq}

mapper.select:
        select * from article p
		order by 1 desc
		LIMIT ${page}, ${pagesize}
